$("body").on("click",".genfaktur",function(e){e.preventDefault();new_faktur();});function new_faktur(){$.post(baseurl+"getnewfaktur",function(data,status){dtx=JSON.parse(data);if(status==='success'){$("#id").prop('value',dtx.idx);$("#kode").prop("value",dtx.kode);$(".genfaktur").addClass('disabled');showform();}})
function showform(){$(".btnsubmit").removeClass('hidden');$(".genfaktur").addClass('disabled');}}